const options = {
  cMapUrl: 'https://cdn.jsdelivr.net/npm/pdfjs-dist@3.4.120/cmaps/',
  cMapPacked: true,
};

export default function PDFViewer({ document }: DocumentViewerProps) {
  const [numPages, setNumPages] = useState<number>(0);
